import React from 'react';
import { motion } from 'framer-motion';

function About() {
  return (
    <section id="about" className="bg-secondary text-white py-5">
      <div className="container">
        <div className="row">
          <div className="col-md-6">
            <h2 className="text-orange">About Me</h2>
            <p>Hello! I’m Vincent Göke, a haptic designer and UX researcher passionate about combining technology and experience to create meaningful interactions.</p>
            <p>Master of Science in Engineering | Audio Designer | Soccer Fan</p>
          </div>
          <div className="col-md-6">
            <img src="./public/images/vincentMain.JPG" className="img-fluid rounded-circle shadow-lg" alt="Vincent Göke" />
          </div>
        </div>
      </div>
    </section>
  );
}

export default About;
