import React, { useRef } from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import { Points, PointMaterial } from '@react-three/drei';
import * as THREE from 'three';

const InteractiveParticles = () => {
  const ref = useRef();
  const particles = new Float32Array(1000).map(() => THREE.MathUtils.randFloatSpread(5)); // Create 1000 random particles

  useFrame((state) => {
    const { mouse } = state;
    ref.current.rotation.x = mouse.y * Math.PI;   // Rotate particles based on mouse Y-axis
    ref.current.rotation.y = mouse.x * Math.PI;   // Rotate particles based on mouse X-axis
  });

  return (
    <Points ref={ref} positions={particles} stride={3}>
      <PointMaterial transparent size={0.1} sizeAttenuation depthWrite={false} color="#F97316" />
    </Points>
  );
};

const Hero = () => (
  <section className="hero-section" style={{ height: '100vh', position: 'relative' }}>
    <Canvas camera={{ position: [0, 0, 5] }}>
      <ambientLight intensity={0.5} />
      <InteractiveParticles />
    </Canvas>
    <div className="hero-content">
      <h1 className="display-3 text-orange">Creative Technologist & Junior UX Designer</h1>
      <p className="lead">Audio Design | UX Design | Haptic Designer</p>
    </div>
  </section>
);

export default Hero;
