import React, { useRef, useState } from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import { OrbitControls } from '@react-three/drei';
import * as THREE from 'three';

// List of geometries to cycle through on click
const geometries = [
  <sphereGeometry args={[0.2, 16, 16]} />,
  <boxGeometry args={[0.3, 0.3, 0.3]} />,
  <coneGeometry args={[0.2, 0.4, 16]} />,
  <torusGeometry args={[0.2, 0.05, 16, 100]} />,
];

const BoundedShapes = () => {
  const groupRef = useRef();
  const [shapeIndex, setShapeIndex] = useState(0);
  const velocities = useRef(
    Array.from({ length: 30 }, () => new THREE.Vector3(
      THREE.MathUtils.randFloat(-0.01, 0.01),
      THREE.MathUtils.randFloat(-0.01, 0.01),
      THREE.MathUtils.randFloat(-0.01, 0.01)
    ))
  );

  const handleClick = () => setShapeIndex((prev) => (prev + 1) % geometries.length);

  useFrame(() => {
    if (groupRef.current) {
      groupRef.current.children.forEach((mesh, i) => {
        mesh.position.add(velocities.current[i]);

        // Bounce back when hitting the boundary [-5, 5] on any axis
        ['x', 'y', 'z'].forEach((axis) => {
          if (mesh.position[axis] > 5 || mesh.position[axis] < -5) {
            velocities.current[i][axis] *= -1; // Reverse direction
          }
        });
      });
    }
  });

  return (
    <group ref={groupRef} onClick={handleClick}>
      {Array.from({ length: 30 }).map((_, i) => (
        <mesh
          key={i}
          position={[
            THREE.MathUtils.randFloatSpread(10),
            THREE.MathUtils.randFloatSpread(10),
            THREE.MathUtils.randFloatSpread(10),
          ]}
        >
          {geometries[shapeIndex]}
          <meshStandardMaterial color={`hsl(${Math.random() * 360}, 70%, 60%)`} />
        </mesh>
      ))}
    </group>
  );
};

const Hero = () => (
  <section className="hero-section" style={{ height: '100vh', position: 'relative', overflow: 'hidden' }}>
    <Canvas camera={{ position: [0, 0, 10] }} style={{ position: 'absolute', top: 0, left: 0, width: '100%', height: '100%' }}>
      <ambientLight intensity={0.7} />
      <pointLight position={[10, 10, 10]} />
      <BoundedShapes />
      <OrbitControls enableZoom={false} />
    </Canvas>
    <div className="hero-content" style={{ position: 'absolute', top: '50%', left: '50%', transform: 'translate(-50%, -50%)', zIndex: 10, textAlign: 'center' }}>
      <h1 className="display-3 text-orange">Creative Technologist & Junior UX Designer</h1>
      <p className="lead">Audio Design | UX Design | Haptic Designer</p>
    </div>
  </section>
);

export default Hero;
