import React, { useRef } from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import { OrbitControls } from '@react-three/drei';
import { motion } from 'framer-motion';

const RotatingCube = () => {
  const ref = useRef();

  // Rotate the cube on each frame
  useFrame(() => {
    ref.current.rotation.x += 0.01;
    ref.current.rotation.y += 0.01;
  });

  return (
    <mesh ref={ref} scale={1.5}>
      <boxGeometry args={[1, 1, 1]} />
      <meshStandardMaterial color="#F97316" />
    </mesh>
  );
};

const Work = () => {
  const projects = [
    { id: 1, title: 'Fitness Tracker UX Design' },
    { id: 2, title: 'Remote Team Food Delivery Concept' },
  ];

  return (
    <section id="work" className="bg-dark text-white">
      <h2 className="display-3 text-orange text-center py-5">My Work</h2>
      {projects.map((project, index) => (
        <motion.div
          key={project.id}
          className="project-section vh-100 d-flex align-items-center"
          initial={{ opacity: 0, y: 100 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 1 }}
        >
          <div className="container">
            <div className={`row ${index % 2 === 0 ? '' : 'flex-row-reverse'}`}>
              <div className="col-md-6">
                <Canvas camera={{ position: [0, 0, 5] }}>
                  <ambientLight intensity={0.5} />
                  <RotatingCube />
                  <OrbitControls />
                </Canvas>
              </div>
              <div className="col-md-6 d-flex flex-column justify-content-center">
                <h3 className="text-orange display-4">{project.title}</h3>
                <p className="lead">
                  This is a placeholder description. Replace this with the actual project details.
                </p>
              </div>
            </div>
          </div>
        </motion.div>
      ))}
    </section>
  );
};

export default Work;
