import React from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls, useGLTF } from '@react-three/drei';
import { motion } from 'framer-motion';

const ProjectModel = ({ modelPath }) => {
  const { scene } = useGLTF(modelPath);
  return <primitive object={scene} scale={0.5} />;
};

const Work = () => {
  const projects = [
    { id: 1, title: 'Fitness Tracker UX Design', modelPath: '/path-to-your-model-1.gltf' },
    { id: 2, title: 'Remote Team Food Delivery Concept', modelPath: '/path-to-your-model-2.gltf' },
  ];

  return (
    <section id="work" className="bg-dark text-white">
      <h2 className="display-3 text-orange text-center py-5">My Work</h2>
      {projects.map((project, index) => (
        <motion.div
          key={project.id}
          className="project-section vh-100 d-flex align-items-center"
          initial={{ opacity: 0, y: 100 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 1 }}
        >
          <div className="container">
            <div className={`row ${index % 2 === 0 ? '' : 'flex-row-reverse'}`}>
              <div className="col-md-6">
                <Canvas camera={{ position: [0, 0, 5] }}>
                  <ambientLight intensity={0.5} />
                  <ProjectModel modelPath={project.modelPath} />
                  <OrbitControls />
                </Canvas>
              </div>
              <div className="col-md-6 d-flex flex-column justify-content-center">
                <h3 className="text-orange display-4">{project.title}</h3>
                <p className="lead">This is a description of the project, detailing its scope and results.</p>
              </div>
            </div>
          </div>
        </motion.div>
      ))}
    </section>
  );
};

export default Work;
