import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';


function Hero() {
  const [offsetY, setOffsetY] = useState(0);

  const handleScroll = () => {
    setOffsetY(window.scrollY); // Track how much the user has scrolled
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <section
      id="hero"
      className="hero-section"
      style={{
        backgroundImage: `url(${heroBG})`,
        backgroundAttachment: 'fixed', // Parallax effect
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        transform: `translateY(${offsetY * 0.2}px)`, // Adjust the depth of the parallax effect
      }}
    >
      <div className="container position-relative">
        <motion.h1 initial={{ opacity: 0, y: 50 }} animate={{ opacity: 1, y: 0 }} className="display-3 text-orange">
          WhoAmI
        </motion.h1>
        <p className="lead">Haptitian | Master of Science in Engineering | UX Researcher & Haptic Designer</p>
        <motion.a href="#work" whileHover={{ scale: 1.1 }} className="btn btn-primary btn-lg mt-4">
          Contact Me
        </motion.a>
      </div>
    </section>
  );
}

export default Hero;
