import React, { useState } from 'react';
import { Modal } from 'react-bootstrap';
import { Link } from 'react-scroll';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBars, faTimes } from '@fortawesome/free-solid-svg-icons';
import './Header.css';

function Header() {
  const [showModal, setShowModal] = useState(false);
  const [currentProject, setCurrentProject] = useState(null);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  const projects = [
    { id: 1, title: 'Fitness Tracker UX Design', section: 'project1' },
    { id: 2, title: 'Remote Team Food Delivery Concept', section: 'project2' },
    { id: 3, title: 'VR Audio Meditation Experience', section: 'project3' },
    { id: 4, title: 'Haptic Feedback Prototyping Kit', section: 'project4' },
    { id: 5, title: 'Smart Home Audio Controls', section: 'project5' },
    { id: 6, title: 'Podcast Production Studio UX', section: 'project6' }
  ];

  const toggleMobileMenu = () => setIsMobileMenuOpen(!isMobileMenuOpen);

  return (
    <aside className="sidebar-nav">
      <div className={`sidebar ${isMobileMenuOpen ? 'open' : ''}`}>
        <h1 className="text-orange brand-name">Vincent GÃ¶ke</h1>
        <button className="btn text-white toggle-btn" onClick={toggleMobileMenu}>
          <FontAwesomeIcon icon={isMobileMenuOpen ? faTimes : faBars} size="2x" />
        </button>
        <nav className="nav-links">
          <Link to="hero" smooth={true} duration={800} offset={-70} className="nav-dot" title="Home">
            <span>Home</span>
          </Link>
          <Link to="work" smooth={true} duration={800} offset={-70} className="nav-dot" title="My Work">
            <span>My Work</span>
          </Link>
          {projects.map((project) => (
            <Link
              key={project.id}
              to={project.section}
              smooth={true}
              duration={800}
              offset={-70}
              className="nav-dot"
              title={project.title}
            >
              <span>{project.title}</span>
            </Link>
          ))}
          <Link to="about" smooth={true} duration={800} offset={-70} className="nav-dot" title="About">
            <span>About</span>
          </Link>
          <Link to="contact" smooth={true} duration={800} offset={-70} className="nav-dot" title="Contact">
            <span>Contact</span>
          </Link>
        </nav>
      </div>
    </aside>
  );
}

export default Header;
