import React, { useRef, Suspense } from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import { Points, PointMaterial, OrbitControls, useGLTF } from '@react-three/drei';
import * as THREE from 'three';

const FallbackMesh = () => (
  <mesh>
    <boxGeometry args={[1, 1, 1]} />
    <meshBasicMaterial color="gray" />
  </mesh>
);

const MyModel = () => {
  const { scene } = useGLTF(`${process.env.PUBLIC_URL}/models/cassette_player_4k.gltf`);
  return <primitive object={scene} scale={1.2} />;
};

const Hero = () => {
  const [modelLoaded, setModelLoaded] = React.useState(false);

  return (
    <section className="hero-section" style={{ height: '100vh', position: 'relative', overflow: 'hidden' }}>
      <Canvas camera={{ position: [0, 0, 10] }} onCreated={() => setModelLoaded(true)}>
        <ambientLight intensity={0.5} />
        <Suspense fallback={null}>
          <MyModel />
        </Suspense>
        <OrbitControls enableZoom={false} />
      </Canvas>
      {!modelLoaded && <p style={{ position: 'absolute', top: '10px', left: '10px', color: 'white' }}>Loading model...</p>}
    </section>
  );
};


export default Hero;
