import React from 'react';
import './Hero.css'; // Create this CSS module for Hero-specific styles

const Hero = () => (
  <section className="hero-section">
    <div className="background-overlay"></div>
    <div className="hero-content text-center">
      <h1 className="display-3">Creative Technologist & Junior UX Designer</h1>
      <p className="lead">Audio Design | UX Design | Haptic Designer</p>
      <a href="#work" className="btn btn-primary mt-3">Explore My Work</a>
    </div>
  </section>
);

export default Hero;
