import React, { useRef } from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import { OrbitControls } from '@react-three/drei';
import * as THREE from 'three';

const InteractiveSpheres = () => {
  const groupRef = useRef();

  useFrame((state) => {
    const { mouse } = state;
    if (groupRef.current) {
      groupRef.current.rotation.x = mouse.y * Math.PI;
      groupRef.current.rotation.y = mouse.x * Math.PI;
    }
  });

  return (
    <group ref={groupRef}>
      {Array.from({ length: 50 }).map((_, i) => (
        <mesh key={i} position={[THREE.MathUtils.randFloatSpread(10), THREE.MathUtils.randFloatSpread(10), THREE.MathUtils.randFloatSpread(10)]}>
          <sphereGeometry args={[0.1, 16, 16]} />
          <meshStandardMaterial color={`hsl(${Math.random() * 360}, 70%, 50%)`} />
        </mesh>
      ))}
    </group>
  );
};

const Hero = () => (
  <section className="hero-section" style={{ height: '100vh', position: 'relative', overflow: 'hidden' }}>
    <Canvas camera={{ position: [0, 0, 10] }} style={{ position: 'absolute', top: 0, left: 0, width: '100%', height: '100%' }}>
      <ambientLight intensity={0.5} />
      <InteractiveSpheres />
      <OrbitControls enableZoom={false} />
    </Canvas>
    <div className="hero-content" style={{ position: 'absolute', top: '50%', left: '50%', transform: 'translate(-50%, -50%)', zIndex: 10, textAlign: 'center' }}>
      <h1 className="display-3 text-orange">Creative Technologist & Junior UX Designer</h1>
      <p className="lead">Audio Design | UX Design | Haptic Designer</p>
    </div>
  </section>
);

export default Hero;
