import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import heroBG from '../assets/css/images/heroBG.png';

const About = () => {
  const [offsetY, setOffsetY] = useState(0);

  const handleScroll = () => {
    setOffsetY(window.scrollY);
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <section
      id="about"
      className="about-section"
      style={{
        position: 'relative',
        backgroundImage: `url(${heroBG})`,
        backgroundSize: 'cover',
        backgroundAttachment: 'fixed',
        backgroundPosition: 'center',
      }}
    >
      {/* Overlay for improved text readability */}
      <div
        style={{
          position: 'absolute',
          top: 0,
          left: 0,
          width: '100%',
          height: '100%',
          backgroundColor: 'rgba(0, 0, 0, 0.5)', // Semi-transparent black overlay
        }}
      ></div>

      <motion.div
        className="container"
        initial={{ opacity: 0, y: 50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1 }}
        style={{
          position: 'relative',
          padding: '100px 0',
          color: 'white',
        }}
      >
        <h2 className="text-orange display-4">About Me</h2>
        <p className="lead">
          Hi! I'm Vincent GÃ¶ke, a passionate haptic designer and UX researcher with a background in human-computer interaction. I focus on creating innovative, emotionally engaging, and intuitive user experiences through haptic design and sensory-based interactions.
        </p>
        <p className="lead">
          My work spans across various fields, from developing haptic feedback prototyping kits for designers to creating immersive audio experiences in virtual reality and context-aware smart home audio systems. I thrive at the intersection of technology and human experience, always aiming to bridge the digital and physical worlds seamlessly.
        </p>
      </motion.div>
    </section>
  );
};

export default About;
