import React, { useState } from 'react';
import { Modal, Navbar, Nav, NavDropdown } from 'react-bootstrap';
import { Link } from 'react-scroll';
import './Header.css'; // Optional for additional custom styles

function Header() {
  const [showModal, setShowModal] = useState(false);
  const [currentProject, setCurrentProject] = useState(null);

  const projects = [
    { id: 1, title: 'Fitness Tracker UX Design', description: 'User-centered design for fitness tracking apps.' },
    { id: 2, title: 'Remote Team Food Delivery Concept', description: 'Video prototype for work-from-home teams.' },
    { id: 3, title: 'VR Audio Meditation Experience', description: 'Immersive soundscapes for mindfulness.' },
    { id: 4, title: 'Haptic Feedback Prototyping Kit', description: 'Creating tools for haptic experience designers.' },
    { id: 5, title: 'Smart Home Audio Controls', description: 'Context-aware audio systems for smart living.' },
    { id: 6, title: 'Podcast Production Studio UX', description: 'Redesigning the workflow for podcast creators.' }
  ];

  const handleShowModal = (project) => {
    setCurrentProject(project);
    setShowModal(true);
  };

  return (
    <header className="sticky-top bg-dark">
      <Navbar expand="lg" variant="dark" className="container">
        <Navbar.Brand href="#home" className="text-orange">Vincent GÃ¶ke</Navbar.Brand>
        <Navbar.Toggle aria-controls="navbar-nav" />
        <Navbar.Collapse id="navbar-nav">
          <Nav className="ml-auto">
            <Nav.Link as={Link} to="hero" smooth={true} duration={800} className="text-white mx-2">
              Home
            </Nav.Link>
            <NavDropdown title="My Work" id="work-dropdown" className="text-white mx-2">
              {projects.map((project) => (
                <NavDropdown.Item key={project.id} onClick={() => handleShowModal(project)}>
                  {project.title}
                </NavDropdown.Item>
              ))}
            </NavDropdown>
            <Nav.Link as={Link} to="about" smooth={true} duration={800} className="text-white mx-2">
              About
            </Nav.Link>
            <Nav.Link as={Link} to="contact" smooth={true} duration={800} className="text-white mx-2">
              Contact
            </Nav.Link>
          </Nav>
        </Navbar.Collapse>
      </Navbar>

      {/* Modal for project details */}
      <Modal show={showModal} onHide={() => setShowModal(false)} centered>
        <Modal.Header closeButton>
          <Modal.Title>{currentProject?.title}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <p>{currentProject?.description}</p>
          <p>In this project, I focused on user-centered design principles to deliver impactful solutions.</p>
        </Modal.Body>
      </Modal>
    </header>
  );
}

export default Header;
