import React, { useRef } from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import { Points, PointMaterial } from '@react-three/drei';
import * as THREE from 'three';

const InteractiveParticles = () => {
  const ref = useRef();

  // Generate 1000 random particle positions
  const particles = new Float32Array(1000).map(() => THREE.MathUtils.randFloatSpread(5));

  // Animate the particles
  useFrame((state) => {
    const { mouse, clock } = state;

    if (ref.current) {
      // Slow floating movement
      ref.current.rotation.x += 0.0005;
      ref.current.rotation.y += 0.0005;

      // Mouse interaction effect: Particles rotate towards the mouse position
      ref.current.rotation.x += (mouse.y * Math.PI - ref.current.rotation.x) * 0.05;
      ref.current.rotation.y += (mouse.x * Math.PI - ref.current.rotation.y) * 0.05;

      // Calculate a color based on mouse direction and time
      const hue = (mouse.x + 1) * 180 + clock.getElapsedTime() * 20;
      ref.current.children[0].material.color.setHSL((hue % 360) / 360, 0.8, 0.5);
    }
  });

  return (
    <Points ref={ref} positions={particles} stride={3}>
      <PointMaterial transparent size={0.15} sizeAttenuation depthWrite={false} />
    </Points>
  );
};

const Hero = () => (
  <section className="hero-section" style={{ height: '100vh', position: 'relative', overflow: 'hidden' }}>
    <Canvas camera={{ position: [0, 0, 10] }}>
      <ambientLight intensity={0.5} />
      <InteractiveParticles />
    </Canvas>
    <div className="hero-content" style={{ position: 'absolute', top: '50%', left: '50%', transform: 'translate(-50%, -50%)', zIndex: 10 }}>
      <h1 className="display-3 text-orange">Creative Technologist & Junior UX Designer</h1>
      <p className="lead">Audio Design | UX Design | Haptic Designer</p>
    </div>
  </section>
);

export default Hero;
