import React, { Suspense } from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls, useGLTF } from '@react-three/drei';

const MyModel = () => {
  const { scene } = useGLTF(`${process.env.PUBLIC_URL}/models/cassette_player_4k.gltf`);
  return <primitive object={scene} scale={1.2} />;
};

const Hero = () => (
  <section className="hero-section" style={{ height: '100vh', position: 'relative', overflow: 'hidden' }}>
    <div className="hero-canvas-wrapper" style={{ height: '50vh', position: 'relative' }}>
      <Canvas camera={{ position: [0, 0, 10] }} style={{ position: 'absolute', top: 0, left: 0, width: '100%', height: '100%' }}>
        <ambientLight intensity={0.5} />
        <Suspense fallback={null}>
          <MyModel />
        </Suspense>
        <OrbitControls enableZoom={false} />
      </Canvas>
    </div>
    <div className="hero-content" style={{ position: 'relative', zIndex: 10, textAlign: 'center', padding: '20px' }}>
      <h1 className="display-3 text-orange">Creative Technologist & Junior UX Designer</h1>
      <p className="lead">Audio Design | UX Design | Haptic Designer</p>
    </div>
  </section>
);

export default Hero;
