import React, { useRef, Suspense } from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import { Points, PointMaterial, OrbitControls, useGLTF } from '@react-three/drei';
import * as THREE from 'three';

const InteractiveParticles = () => {
  const ref = useRef();
  const particles = new Float32Array(500).map(() => THREE.MathUtils.randFloatSpread(5));

  useFrame((state) => {
    if (ref.current) {
      const { mouse } = state;
      ref.current.rotation.x += 0.001;
      ref.current.rotation.y += 0.001;
      ref.current.rotation.x += (mouse.y * Math.PI - ref.current.rotation.x) * 0.01;
      ref.current.rotation.y += (mouse.x * Math.PI - ref.current.rotation.y) * 0.01;
    }
  });

  return (
    <Points ref={ref} positions={particles} stride={3}>
      <PointMaterial transparent size={0.08} sizeAttenuation depthWrite={false} />
    </Points>
  );
};

const MyModel = () => {
  const { scene } = useGLTF(`${process.env.PUBLIC_URL}/models/cassette_player_4k.gltf`);
  return <primitive object={scene} scale={1.2} />;
};

const Hero = () => (
  <section className="hero-section" style={{ height: '100vh', position: 'relative', overflow: 'hidden' }}>
    <Canvas camera={{ position: [0, 0, 10] }}>
      <ambientLight intensity={0.5} />
      <InteractiveParticles />
      <Suspense fallback={<span>Loading model...</span>}>
        <MyModel />
      </Suspense>
      <OrbitControls enableZoom={false} />
    </Canvas>
    <div className="hero-content" style={{ position: 'absolute', top: '50%', left: '50%', transform: 'translate(-50%, -50%)', zIndex: 10 }}>
      <h1 className="display-3 text-orange">Creative Technologist & Junior UX Designer</h1>
      <p className="lead">Audio Design | UX Design | Haptic Designer</p>
    </div>
  </section>
);

export default Hero;
