import React, { useState } from 'react';
import { Modal } from 'react-bootstrap';
import { Link } from 'react-scroll';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBars, faTimes } from '@fortawesome/free-solid-svg-icons';
import './Header.css';

function Header() {
  const [showModal, setShowModal] = useState(false);
  const [currentProject, setCurrentProject] = useState(null);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [dropdownOpen, setDropdownOpen] = useState(false);

  const projects = [
    { id: 1, title: 'Fitness Tracker UX Design', description: 'User-centered design for fitness tracking apps.' },
    { id: 2, title: 'Remote Team Food Delivery Concept', description: 'Video prototype for work-from-home teams.' },
    { id: 3, title: 'VR Audio Meditation Experience', description: 'Immersive soundscapes for mindfulness.' },
    { id: 4, title: 'Haptic Feedback Prototyping Kit', description: 'Creating tools for haptic experience designers.' },
    { id: 5, title: 'Smart Home Audio Controls', description: 'Context-aware audio systems for smart living.' },
    { id: 6, title: 'Podcast Production Studio UX', description: 'Redesigning the workflow for podcast creators.' }
  ];

  const handleShowModal = (project) => {
    setCurrentProject(project);
    setShowModal(true);
  };

  const toggleMobileMenu = () => setIsMobileMenuOpen(!isMobileMenuOpen);
  const toggleDropdown = () => setDropdownOpen(!dropdownOpen);

  return (
    <header className="bg-dark text-white sticky-top">
      <div className="container d-flex justify-content-between align-items-center py-3">
        <h1 className="text-orange brand-name">Vincent Göke</h1>
        <button
          className="d-md-none btn text-white"
          onClick={toggleMobileMenu}
          aria-label="Toggle navigation"
        >
          <FontAwesomeIcon icon={isMobileMenuOpen ? faTimes : faBars} size="2x" />
        </button>
        <div className={`d-md-flex flex-column flex-md-row align-items-center ${isMobileMenuOpen ? 'd-block' : 'd-none d-md-flex'}`}>
          <Link to="hero" smooth={true} duration={800} offset={-70} className="mx-3 nav-link">
            Home
          </Link>
          <Link to="work" smooth={true} duration={800} offset={-70} className="mx-3 nav-link">
            My Work
          </Link>
          <div className="dropdown">
            <button className="btn btn-secondary dropdown-toggle" type="button" onClick={toggleDropdown}>
              More
            </button>
            {dropdownOpen && (
              <ul className="dropdown-menu show">
                <li>
                  <Link to="about" smooth={true} duration={800} offset={-70} className="dropdown-item">
                    About Me
                  </Link>
                </li>
                <li>
                  <Link to="contact" smooth={true} duration={800} offset={-70} className="dropdown-item">
                    Contact
                  </Link>
                </li>
              </ul>
            )}
          </div>
        </div>
      </div>

      <Modal show={showModal} onHide={() => setShowModal(false)} centered>
        <Modal.Header closeButton>
          <Modal.Title>{currentProject?.title}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <p>{currentProject?.description}</p>
          <p>In this project, I focused on user-centered design principles to deliver impactful solutions.</p>
        </Modal.Body>
      </Modal>
    </header>
  );
}

export default Header;
